import { div } from '@tensorflow/tfjs'
import React, { useRef, useState } from 'react' 
import './ImageGenerator.css'
import default_image from '../Assets/default_image.png'
import { storage } from '../../firebase'
import { ref, uploadBytes } from "firebase/storage"
import { v4 } from 'uuid'

const ImageGenerator = () => {

    const [imageUpload, setImageUpload] = useState(null);
    const uploadImage = () => {
        if (imageUpload == null ) return;
        const imageRef = ref(storage, `images/${imageUpload.name + v4()}.png`);
        uploadBytes(imageRef, imageUpload).then(() => {
            alert("Image uploaded!");
        })
    }

    const getBase64Image = (img) => {
        var canvas = document.createElement("canvas");
        canvas.height = img.height;
        var ctx = canvas.getContext("2d");
        ctx.drawImage(img, 0, 0);
        var dataURL = canvas.toDataURL("image/png");
        return dataURL.replace(/^data:image\/?[A-z]*;base64,/);
    }

    const [image_url,setImage_url] = useState("/");
    let inputRef = useRef(null);

    const imageGenerator = async () => {
        if(inputRef.current.value==="") {
            return 0;
        }
        const response = await fetch(
            "https://api.openai.com/v1/images/generations",
            {
                method: "POST",
                headers: {
                    "Content-Type":"application/json",
                    Authorization: "Bearer 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",
                    "User-Agent":"Chrome",
                },
                body:JSON.stringify ({
                    prompt:`${inputRef.current.value}`,
                    n:1,
                    size:"512x512"
                })
            }
        ); 
        let data = await response.json();
        console.log(data);
        let data_array = data.data;
        setImage_url(data_array[0].url);
    }

    return (
        <div className='ai-image-generator'>
            <div className="header">AI Image <span>generator</span></div>
            <div className="img-loading">
            <div className="image"><img id="imageid" src={image_url==="/"?default_image:image_url} alt="" /></div>
            </div>
            <div className="search-box">
                <input type="text" ref={inputRef} className="search-input" placeholder='What is your favorite memory' />
                <div className="generate-btn" onClick={()=>{imageGenerator()}}>Generate</div>
            </div>
            <div className="uploadTest-btn" onClick={()=>{
                setImageUpload(getBase64Image(document.getElementById("imageid")));
                uploadImage();
                }}>Upload</div>

        </div>
    )
}

export default ImageGenerator
